/**
 * Remove leading indents from a multi line string.
 *
 * @example
 *     const val = heredoc`
 *         test
 *         test
 *     `; // val == "test\ntest\n"
 *
 *     const val2 = heredoc(`
 *         test
 *         test
 *     `); // val2 == "test\ntest\n"
 */
export default function heredoc(value: TemplateStringsArray, ...values: any[]): string;
export default function heredoc(value: string): string;
